   * @param to_filter_event method that will be called with the number of events to be sent
   *        on the outgoing event port 'to_filter_event'.
   */
  def check_concrete_output(to_filter_u32: Base_Types.Bits => B = to_filter_u32Param => {T},
                            to_filter_latitude: Base_Types.Bits => B = to_filter_latitudeParam => {T},
                            to_filter_longitude: Base_Types.Bits => B = to_filter_longitudeParam => {T},
                            to_filter_coordinate: Base_Types.Bits => B = to_filter_coordinateParam => {T},
                            to_filter_mission: ISZ[Base_Types.Bits] => B = to_filter_missionParam => {T},
                            to_filter_event: Z => B = to_filter_eventParam => {T}): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    val to_filter_u32Value: Base_Types.Bits = get_to_filter_u32().get
    if(!to_filter_u32(to_filter_u32Value)) {
      testFailures = testFailures :+ st"'to_filter_u32' did not match expected: value of the outgoing data port is ${to_filter_u32Value}"
    }
    val to_filter_latitudeValue: Base_Types.Bits = get_to_filter_latitude().get
    if(!to_filter_latitude(to_filter_latitudeValue)) {
      testFailures = testFailures :+ st"'to_filter_latitude' did not match expected: value of the outgoing data port is ${to_filter_latitudeValue}"
    }
    val to_filter_longitudeValue: Base_Types.Bits = get_to_filter_longitude().get
    if(!to_filter_longitude(to_filter_longitudeValue)) {
      testFailures = testFailures :+ st"'to_filter_longitude' did not match expected: value of the outgoing data port is ${to_filter_longitudeValue}"
    }
    val to_filter_coordinateValue: Base_Types.Bits = get_to_filter_coordinate().get
    if(!to_filter_coordinate(to_filter_coordinateValue)) {
      testFailures = testFailures :+ st"'to_filter_coordinate' did not match expected: value of the outgoing data port is ${to_filter_coordinateValue}"
    }
    var to_filter_missionValue: ISZ[Base_Types.Bits] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_to_filter_mission().nonEmpty) to_filter_missionValue = to_filter_missionValue :+ get_to_filter_mission().get
    if(!to_filter_mission(to_filter_missionValue)) {
      testFailures = testFailures :+ st"'to_filter_mission' did not match expected: received ${to_filter_missionValue.size} events with the following payloads ${to_filter_missionValue}"
    }
    // TODO: event port getter should return the number of events in
    //       the output queue when queue sizes > 1 support is added to ART
    val to_filter_eventValue: Z = if(get_to_filter_event().nonEmpty) z"1" else z"0"
    if(!to_filter_event(to_filter_eventValue)) {
      testFailures = testFailures :+ st"'to_filter_event' did not match expected: ${to_filter_eventValue} events were in the outgoing event queue"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // getter for out DataPort
