void sb_freeze_event_port_from_producer_event() {
  sb_event_counter_t current_sb_from_producer_event_counter;

  sb_from_producer_event_received_events = 0; // drop any events not handled during last dispatch

  // get current shared counter value
  current_sb_from_producer_event_counter = *sb_from_producer_event_counter;

  // Acquire memory fence - ensure preceding read occurs before any subsequent read or write
  sb_from_producer_event_counter_acquire();

  // NOTE: Counters can wrap, so we must use != below instead of >
  while(current_sb_from_producer_event_counter != sb_from_producer_event_last_counter){
    sb_from_producer_event_last_counter++;
    sb_from_producer_event_received_events++;
  }

  if(sb_from_producer_event_received_events > 0) {

    // from_producer_event's queue size is 1
    if(sb_from_producer_event_received_events > 1) {
      //printf("Filter: dropping %i event(s) from incoming event port from_producer_event\n", (sb_from_producer_event_received_events - 1));

      // drop events
      sb_from_producer_event_received_events = 1;
    }
  }
}

