/************************************************************************
 *
 * Static variables and queue management functions for event port:
 *     from_filter_event
 *
 ************************************************************************/
static sb_event_counter_t sb_from_filter_event_received_events = 0;
static sb_event_counter_t sb_from_filter_event_last_counter = 0;

/************************************************************************
 * sb_from_filter_event_dequeue:
 * Invoked from local active thread.
 *
 * This is the function invoked by the active thread to decrement the
 * input event index.
 *
 ************************************************************************/
bool sb_from_filter_event_dequeue() {
  if(sb_from_filter_event_received_events > 0) {
    sb_from_filter_event_received_events--;
    return true;
  } else {
    return false;
  }
}

/************************************************************************
 * sb_from_filter_event_is_empty;
 *
 * Helper method to determine if infrastructure port has not received
 * any new events since the last dispatch
 *
 ************************************************************************/
bool sb_from_filter_event_is_empty() {
  return sb_from_filter_event_received_events == 0;
}

void sb_freeze_event_port_from_filter_event() {
  sb_event_counter_t current_sb_from_filter_event_counter;

  sb_from_filter_event_received_events = 0; // drop any events not handled during last dispatch

  // get current shared counter value
  current_sb_from_filter_event_counter = *sb_from_filter_event_counter;

  // Acquire memory fence - ensure preceding read occurs before any subsequent read or write
  sb_from_filter_event_counter_acquire();

  // NOTE: Counters can wrap, so we must use != below instead of >
  while(current_sb_from_filter_event_counter != sb_from_filter_event_last_counter){
    sb_from_filter_event_last_counter++;
    sb_from_filter_event_received_events++;
  }

  if(sb_from_filter_event_received_events > 0) {

    // from_filter_event's queue size is 1
    if(sb_from_filter_event_received_events > 1) {
      //printf("Consumer: dropping %i event(s) from incoming event port from_filter_event\n", (sb_from_filter_event_received_events - 1));

      // drop events
      sb_from_filter_event_received_events = 1;
    }
  }
}

