  def stepSystemNSlotsIMP(numSlots :Z): Unit = {
    // pre-condition
    assert(numSlots > 0)
    // pre-condition (invariants on scheduleState and dScheduleSpec)
    assert(validState(scheduleState, dScheduleSpec))
