component emitter_t_impl_testepmon_src {
  include <sb_types.h>;
  include <sb_queue_int8_t_1.h>;
  control;
  emits ReceiveEvent sb_enq_1_notification;
  dataport sb_queue_int8_t_1_t sb_enq_queue_1;
  has semaphore sb_dispatch_sem;
}

component consumer_t_impl_testepmon_dest {
  include <sb_types.h>;
  include <sb_queue_int8_t_1.h>;
  control;
  consumes ReceiveEvent sb_deq_notification;
  dataport sb_queue_int8_t_1_t sb_deq_queue;
  has semaphore sb_dispatch_sem;
}

// This file will be regenerated, do not edit

import <std_connector.camkes>;
import "components/emitter_t_impl_testepmon_src/emitter_t_impl_testepmon_src.camkes";
import "components/consumer_t_impl_testepmon_dest/consumer_t_impl_testepmon_dest.camkes";

assembly {
  composition {
    component emitter_t_impl_testepmon_src testepmon_src;
    component consumer_t_impl_testepmon_dest testepmon_dest;

    connection seL4Notification conn1(from testepmon_src.sb_enq_1_notification, to testepmon_dest.sb_deq_notification);
    connection seL4SharedData conn2(from testepmon_src.sb_enq_queue_1, to testepmon_dest.sb_deq_queue);
  }

  configuration {
    testepmon_src.sb_enq_queue_1_access = "W";
    testepmon_dest.sb_deq_queue_access = "R";

  }
}

/* testepmon/components/emitter/src/emitter.c */

#include <camkes.h>
#include <stdio.h>
#include <sb_types.h>
#include <sb_emitter_t_impl.h>

static int8_t _value;

void testepmon_emitter_component_init(const int64_t *in_arg)
{
    printf("testepmon_emitter_component_init called\n");
    _value = 0;
}

/* control thread: keep calling enqueue for thing
 */
void run_emitter(const int64_t *in_arg)
{
    if (sb_enq_enqueue( &_value ) ) {
        printf("[source] Sent %d\n", _value);
        _value = (_value + 1) % 500;
    } else {
        printf("[source] Unable to send\n");
    }
}

/* testepmon/components/consumer/src/consumer */

#include <camkes.h>
#include <stdio.h>
#include <sb_types.h>
#include <sb_consumer_t_impl.h>

void testepmon_consumer_component_init(const int64_t *in_arg)
{
    printf("testepmon_consumer_component_init called\n");
}


/* Handle monsig notification: there is QueuedData
 */
void testepmon_consumer_s_event_handler(const int8_t * in_arg)
{
    /* keep dequeuing until no more things can be had
     */
    int8_t value;

    if (sb_deq_dequeue(&value)) {
        printf("[destination] value {%d}\n", value);
    } else {
        printf("[destination] no value consumed.\n");
    }
}

