 * sb_entrypoint_consumer_t_impl_deq:
 *
 * This is the function invoked by an active thread dispatcher to
 * call to a user-defined entrypoint function.  It sets up the dispatch
 * context for the user-defined entrypoint, then calls it.
 *
 ************************************************************************/
void sb_entrypoint_consumer_t_impl_deq(const int8_t * in_arg) {
  testepmon_consumer_s_event_handler((int8_t *) in_arg);
}


/************************************************************************
 *  sb_entrypoint_consumer_t_impl_testepmon_dest_initializer:
 *
 * This is the function invoked by an active thread dispatcher to
 * call to a user-defined entrypoint function.  It sets up the dispatch
 * context for the user-defined entrypoint, then calls it.
 *
 ************************************************************************/
void sb_entrypoint_consumer_t_impl_testepmon_dest_initializer(const int64_t * in_arg) {
  testepmon_consumer_component_init((int64_t *) in_arg);
}

void pre_init(void) {
  // initialise data structure for incoming event data port deq
  sb_queue_int8_t_1_Recv_init(&sb_deq_recv_queue, sb_deq_queue);
}

void post_init(void) {
  // register callback for EventDataPort port deq
  CALLBACKOP(sb_deq_notification_reg_callback(sb_deq_notification_handler, NULL));
}

/************************************************************************
 * int run(void)
 * Main active thread function.
 ************************************************************************/
int run(void) {

  {
    int64_t sb_dummy;
    sb_entrypoint_consumer_t_impl_testepmon_dest_initializer(&sb_dummy);
  }
  MUTEXOP(sb_dispatch_sem_wait())
  for(;;) {
    MUTEXOP(sb_dispatch_sem_wait())
    {
      int8_t sb_deq;
      while (sb_deq_dequeue((int8_t *) &sb_deq)) {
        sb_entrypoint_consumer_t_impl_deq(&sb_deq);
      }
    }
  }
  return 0;
}

