void run_emitter(const int64_t *in_arg) {
  for(int32_t i = 0; i < counter; i++) {
    sb_e_enqueue();
  }
  printf("---------------------------------------\n");
  printf("[%s] Sent %i events.\n", get_instance_name(), counter);

  counter = (counter + 1) % 7; // send b/w 0 to 6 events per dispatch
}

