                            temp: Data_Invariants.Temperature_i => B = tempParam => {T}): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    val setPointValue: Data_Invariants.SetPoint_i = get_setPoint().get
    if(!setPoint(setPointValue)) {
      testFailures = testFailures :+ st"'setPoint' did not match expected: value of the outgoing data port is ${setPointValue}"
    }
    val tempValue: Data_Invariants.Temperature_i = get_temp().get
    if(!temp(tempValue)) {
      testFailures = testFailures :+ st"'temp' did not match expected: value of the outgoing data port is ${tempValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // getter for out DataPort
  def get_setPoint(): Option[Data_Invariants.SetPoint_i] = {
    val value: Option[Data_Invariants.SetPoint_i] = get_setPoint_payload() match {
