      case Some(v) => fail(s"Unexpected payload on port setPoint.  Expecting 'Data_Invariants.SetPoint_i_Payload' but received ${v}")
      case _ => None[Data_Invariants.SetPoint_i]()
    }
    return value
  }

  // payload getter for out DataPort
  def get_setPoint_payload(): Option[Data_Invariants.SetPoint_i_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.setPoint_Id).asInstanceOf[Option[Data_Invariants.SetPoint_i_Payload]]
  }

  // getter for out DataPort
  def get_temp(): Option[Data_Invariants.Temperature_i] = {
