abstract class Producer_proc_producer_TestApi extends BridgeTestSuite[Producer_proc_producer_Bridge](Arch.Bit_Codec_Sys_Impl_Instance_proc_producer) {

  /** helper function to check Producer_proc_producer's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param to_filter_bool method that will be called with the value of the outgoing data
   *        port 'to_filter_bool'.
   * @param to_filter_u32 method that will be called with the value of the outgoing data
   *        port 'to_filter_u32'.
   * @param to_filter_string method that will be called with the value of the outgoing data
   *        port 'to_filter_string'.
   * @param to_filter_mission method that will be called with the payloads to be sent
   *        on the outgoing event data port 'to_filter_mission'.
   * @param to_filter_event method that will be called with the number of events to be sent
   *        on the outgoing event port 'to_filter_event'.
   */
  def check_concrete_output(to_filter_bool: Base_Types.Bits => B = to_filter_boolParam => {T},
                            to_filter_u32: Base_Types.Bits => B = to_filter_u32Param => {T},
                            to_filter_string: Base_Types.Bits => B = to_filter_stringParam => {T},
                            to_filter_mission: ISZ[Base_Types.Bits] => B = to_filter_missionParam => {T},
                            to_filter_event: Z => B = to_filter_eventParam => {T}): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    val to_filter_boolValue: Base_Types.Bits = get_to_filter_bool().get
    if(!to_filter_bool(to_filter_boolValue)) {
      testFailures = testFailures :+ st"'to_filter_bool' did not match expected: value of the outgoing data port is ${to_filter_boolValue}"
    }
    val to_filter_u32Value: Base_Types.Bits = get_to_filter_u32().get
    if(!to_filter_u32(to_filter_u32Value)) {
      testFailures = testFailures :+ st"'to_filter_u32' did not match expected: value of the outgoing data port is ${to_filter_u32Value}"
    }
    val to_filter_stringValue: Base_Types.Bits = get_to_filter_string().get
    if(!to_filter_string(to_filter_stringValue)) {
      testFailures = testFailures :+ st"'to_filter_string' did not match expected: value of the outgoing data port is ${to_filter_stringValue}"
    }
    var to_filter_missionValue: ISZ[Base_Types.Bits] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_to_filter_mission().nonEmpty) to_filter_missionValue = to_filter_missionValue :+ get_to_filter_mission().get
    if(!to_filter_mission(to_filter_missionValue)) {
      testFailures = testFailures :+ st"'to_filter_mission' did not match expected: received ${to_filter_missionValue.size} events with the following payloads ${to_filter_missionValue}"
    }
    // TODO: event port getter should return the number of events in
    //       the output queue when queue sizes > 1 support is added to ART
    val to_filter_eventValue: Z = if(get_to_filter_event().nonEmpty) z"1" else z"0"
    if(!to_filter_event(to_filter_eventValue)) {
      testFailures = testFailures :+ st"'to_filter_event' did not match expected: ${to_filter_eventValue} events were in the outgoing event queue"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // getter for out DataPort
  def get_to_filter_bool(): Option[Base_Types.Bits] = {
    val value: Option[Base_Types.Bits] = get_to_filter_bool_payload() match {
      case Some(Base_Types.Bits_Payload(v)) => Some(v)
      case Some(v) => fail(s"Unexpected payload on port to_filter_bool.  Expecting 'Base_Types.Bits_Payload' but received ${v}")
      case _ => None[Base_Types.Bits]()
    }
    return value
  }

  // payload getter for out DataPort
  def get_to_filter_bool_payload(): Option[Base_Types.Bits_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.to_filter_bool_Id).asInstanceOf[Option[Base_Types.Bits_Payload]]
  }

  // getter for out DataPort
  def get_to_filter_u32(): Option[Base_Types.Bits] = {
    val value: Option[Base_Types.Bits] = get_to_filter_u32_payload() match {
      case Some(Base_Types.Bits_Payload(v)) => Some(v)
      case Some(v) => fail(s"Unexpected payload on port to_filter_u32.  Expecting 'Base_Types.Bits_Payload' but received ${v}")
      case _ => None[Base_Types.Bits]()
    }
    return value
  }

  // payload getter for out DataPort
  def get_to_filter_u32_payload(): Option[Base_Types.Bits_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.to_filter_u32_Id).asInstanceOf[Option[Base_Types.Bits_Payload]]
  }

  // getter for out DataPort
  def get_to_filter_string(): Option[Base_Types.Bits] = {
    val value: Option[Base_Types.Bits] = get_to_filter_string_payload() match {
      case Some(Base_Types.Bits_Payload(v)) => Some(v)
      case Some(v) => fail(s"Unexpected payload on port to_filter_string.  Expecting 'Base_Types.Bits_Payload' but received ${v}")
      case _ => None[Base_Types.Bits]()
    }
    return value
  }

  // payload getter for out DataPort
  def get_to_filter_string_payload(): Option[Base_Types.Bits_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.to_filter_string_Id).asInstanceOf[Option[Base_Types.Bits_Payload]]
  }

  // getter for out EventDataPort
  def get_to_filter_mission(): Option[Base_Types.Bits] = {
    val value: Option[Base_Types.Bits] = get_to_filter_mission_payload() match {
      case Some(Base_Types.Bits_Payload(v)) => Some(v)
      case Some(v) => fail(s"Unexpected payload on port to_filter_mission.  Expecting 'Base_Types.Bits_Payload' but received ${v}")
      case _ => None[Base_Types.Bits]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_to_filter_mission_payload(): Option[Base_Types.Bits_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.to_filter_mission_Id).asInstanceOf[Option[Base_Types.Bits_Payload]]
  }

  // getter for out EventPort
  def get_to_filter_event(): Option[art.Empty] = {
    val value: Option[art.Empty] = get_to_filter_event_payload() match {
      case Some(Empty()) => Some(Empty())
      case Some(v) => fail(s"Unexpected payload on port to_filter_event.  Expecting 'Empty' but received ${v}")
      case _ => None[art.Empty]()
    }
    return value
  }

  // payload getter for out EventPort
  def get_to_filter_event_payload(): Option[Empty] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.to_filter_event_Id).asInstanceOf[Option[Empty]]
  }

}

