  val dataPortVariables: MMap[Art.PortId, ArtMessage] = concMap()
  val receivedPortValues: MMap[Art.PortId, ArtMessage] = concMap()
  val sentPortValues: MMap[Art.PortId, ArtMessage] = concMap()

