import art.DispatchPropertyProtocol._
import art.PortMode._
import building_control_gen_mixed._
import building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_seL4Nix

object fan extends App {

  val fanBridge : building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge = {
    val fanCmd = Port[BuildingControl.FanCmd.Type] (id = 0, name = "BuildingControlDemo_i_Instance_tcp_fan_fanCmd", mode = EventIn)
    val fanAck = Port[BuildingControl.FanAck.Type] (id = 1, name = "BuildingControlDemo_i_Instance_tcp_fan_fanAck", mode = EventOut)

    building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge(
      id = 0,
      name = "BuildingControlDemo_i_Instance_tcp_fan",
      dispatchProtocol = Sporadic(min = 1000),
      dispatchTriggers = None(),

      fanCmd = fanCmd,
      fanAck = fanAck
    )
  }

  val entryPoints: Bridge.EntryPoints = fanBridge.entryPoints
  val noData: Option[DataContent] = None()

  // fanCmd: In EventDataPort BuildingControl.FanCmd
  val fanCmd_id: Art.PortId = fanBridge.fanCmd.id
  var fanCmd_port: Option[DataContent] = noData

  // fanAck: Out EventDataPort BuildingControl.FanAck
  val fanAck_id: Art.PortId = fanBridge.fanAck.id
  var fanAck_port: Option[DataContent] = noData

  def dispatchStatus(bridgeId: Art.BridgeId): DispatchStatus = {
    var portIds: ISZ[Art.PortId] = ISZ()
    if(!Fan_i_tcp_fan_seL4Nix.fanCmd_IsEmpty()) {
      portIds = portIds :+ fanCmd_id
    }
    return EventTriggered(portIds)
  }

  def getValue(portId: Art.PortId): Option[DataContent] = {
    if(portId == fanCmd_id) {
      return fanCmd_port
    } else {
      halt(s"Unexpected: fan.getValue called with: ${portId}")
    }
  }

  def receiveInput(eventPortIds: ISZ[Art.PortId], dataPortIds: ISZ[Art.PortId]): Unit = {
    // ignore params

    fanCmd_port = Fan_i_tcp_fan_seL4Nix.fanCmd_Receive()
  }

  def putValue(portId: Art.PortId, data: DataContent): Unit = {
    if(portId == fanAck_id) {
      fanAck_port = Some(data)
    } else {
      halt(s"Unexpected: fan.putValue called with: ${portId}")
    }
  }

  def sendOutput(eventPortIds: ISZ[Art.PortId], dataPortIds: ISZ[Art.PortId]): Unit = {
    // ignore params

    if(fanAck_port.nonEmpty) {
      Fan_i_tcp_fan_seL4Nix.fanAck_Send(fanAck_port.get)
      fanAck_port = noData
    }
  }

  def initialiseArchitecture(): Unit = {
    val ad = ArchitectureDescription(
      components = MSZ (fanBridge),
      connections = ISZ ()
    )
    Art.run(ad)
  }

  def initialiseEntryPoint(): Unit = { entryPoints.initialise() }

  def computeEntryPoint(): Unit = { entryPoints.compute() }

  def finaliseEntryPoint(): Unit = { entryPoints.finalise() }

  def main(args: ISZ[String]): Z = {

    // need to touch the following for transpiler
    initialiseArchitecture()
    initialiseEntryPoint()
    computeEntryPoint()
    finaliseEntryPoint()

    touch()

    return 0
  }

  def touch(): Unit = {
    if(F) {
      TranspilerToucher.touch()

      // touch each payload/type in case some are only used as a field in a record
      def printDataContent(a: art.DataContent): Unit = { println(s"${a}") }

      printDataContent(Base_Types.Float_32_Payload(Base_Types.Float_32_empty()))
      printDataContent(BuildingControl.TempUnit_Payload(BuildingControl.TempUnit.byOrdinal(0).get))
      printDataContent(BuildingControl.Temperature_impl_Payload(BuildingControl.Temperature_impl.empty()))
      printDataContent(BuildingControl.SetPoint_impl_Payload(BuildingControl.SetPoint_impl.empty()))
      printDataContent(BuildingControl.FanAck_Payload(BuildingControl.FanAck.byOrdinal(0).get))
      printDataContent(BuildingControl.FanCmd_Payload(BuildingControl.FanCmd.byOrdinal(0).get))
      printDataContent(art.Empty())

      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_initialization_api.get.logInfo("")
      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_initialization_api.get.logDebug("")
      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_initialization_api.get.logError("")
      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_operational_api.get.logInfo("")
      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_operational_api.get.logDebug("")
      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_operational_api.get.logError("")
      val apiUsage_fanCmd: Option[BuildingControl.FanCmd.Type] = building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_operational_api.get.getfanCmd()
      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_initialization_api.get.sendfanAck(BuildingControl.FanAck.byOrdinal(0).get)
      building_control_gen_mixed.BuildingControl.Fan_i_tcp_fan_Bridge.c_operational_api.get.sendfanAck(BuildingControl.FanAck.byOrdinal(0).get)
    }
  }

  def logInfo(title: String, msg: String): Unit = {
    print(title)
    print(": ")
    println(msg)
  }

  def logError(title: String, msg: String): Unit = {
    eprint(title)
    eprint(": ")
    eprintln(msg)
  }

  def logDebug(title: String, msg: String): Unit = {
    print(title)
    print(": ")
    println(msg)
  }

  def run(): Unit = {}

}

