import <std_connector.camkes>;
import "components/emitter_impl_test_event_port_src/emitter_impl_test_event_port_src.camkes";
import "components/consumer_impl_test_event_port_snk/consumer_impl_test_event_port_snk.camkes";

assembly {
  composition {
    component emitter_impl_test_event_port_src test_event_port_src;
    component consumer_impl_test_event_port_snk test_event_port_snk;

    connection seL4Notification conn1(from test_event_port_src.sb_e, to test_event_port_snk.sb_s);
    connection seL4SharedData conn2(from test_event_port_src.sb_e_counter, to test_event_port_snk.sb_s_counter);
  }

  configuration {
    test_event_port_src.sb_e_counter_access = "W";
    test_event_port_snk.sb_s_counter_access = "R";

  }
}

#include <camkes.h>
#include <stdio.h>
#include <sb_emitter_impl.h>

void test_event_port_emitter_component_init(const int64_t *in_arg) {
  printf("[%s] test_event_port_emitter_component_init called\n", get_instance_name());
}

void run_emitter(const int64_t *in_arg) {
  printf("---------------------------------------\n");
  sb_e_enqueue();
  printf("[%s] Sent event.\n", get_instance_name());
}

#include <camkes.h>
#include <stdio.h>
#include <sb_consumer_impl.h>

static int8_t _value;

void test_event_port_consumer_component_init(const int64_t *in_arg) {
  printf("[%s] test_event_port_consumer_component_init called\n", get_instance_name());
  _value = 0;
}

void test_event_port_consumer_s_event_handler() {
  int8_t old_value = _value;
  while(sb_s_dequeue()) {
    _value = (_value + 1) % 500;
  }

  printf("[%s] Callback fired.  Received %i events this dispatch, %i total\n", get_instance_name(), (_value - old_value), _value);
}

