component source_t_impl_testdpmon_src {
  include <sb_types.h>;
  include <sp_int8_t.h>;
  control;
  dataport sp_int8_t_t sb_write_port;
  has semaphore sb_dispatch_sem;
}

component destination_t_impl_testdpmon_dest {
  include <sb_types.h>;
  include <sp_int8_t.h>;
  control;
  dataport sp_int8_t_t sb_read_port;
  has semaphore sb_dispatch_sem;
}

