import <std_connector.camkes>;
import "components/source_t_impl_testdpmon_src/source_t_impl_testdpmon_src.camkes";
import "components/destination_t_impl_testdpmon_dest/destination_t_impl_testdpmon_dest.camkes";

assembly {
  composition {
    component source_t_impl_testdpmon_src testdpmon_src;
    component destination_t_impl_testdpmon_dest testdpmon_dest;

    connection seL4SharedData conn1(from testdpmon_src.sb_write_port, to testdpmon_dest.sb_read_port);
  }

  configuration {
    testdpmon_src.write_port_access = "W";
    testdpmon_dest.read_port_access = "R";

  }
}

