procedure sb_Monitor_int8_t_1 {
  include <sb_types.h>;
  bool is_empty();
  bool enqueue(refin int8_t m);
  bool dequeue(out int8_t m);
};

#ifndef SB_VERIFY
#include <stdio.h>
#endif // SB_VERIFY

#include <sb_types.h>
#include <sb_dest_deq_Monitor.h>

int mon_get_sender_id(void);
int monsig_emit(void);

int8_t contents[1];
static uint32_t front = 0;
static uint32_t length = 0;

static bool is_full(void) {
  return length == 1;
}

bool mon_is_empty(void) {
  return length == 0;
}

bool mon_dequeue(int8_t * m) {
  if (mon_is_empty()) {
    return false;
  } else {
    *m = contents[front];
    front = (front + 1) % 1;
    length--;
    return true;
  }
}

bool mon_enqueue(const int8_t * m) {
  if (is_full()) {
    return false;
  } else {
    contents[(front + length) % 1] = *m;
    length++;
    monsig_emit();
    return true;
  }
}

