abstract class Radio_Impl_MCMP_RADIO_HW_TestApi extends BridgeTestSuite[Radio_Impl_MCMP_RADIO_HW_Bridge](Arch.UAV_Impl_Instance_MCMP_RADIO_HW) {

  /** helper function to set the values of all input ports.
   * @param recv_map_inn payloads for event data port recv_map_inn.
   *   ART currently supports single element event data queues so
   *   only the last element of recv_map_inn will be used
   * @param send_status_inn payloads for event data port send_status_inn.
   *   ART currently supports single element event data queues so
   *   only the last element of send_status_inn will be used
   */
  def put_concrete_inputs(recv_map_inn : ISZ[MC.MISSING_TYPE_Impl],
                          send_status_inn : ISZ[SW.Coordinate_Impl]): Unit = {
    for(v <- recv_map_inn){
      put_recv_map_inn(v)
    }
    for(v <- send_status_inn){
      put_send_status_inn(v)
    }
  }


  /** helper function to check Radio_Impl_MCMP_RADIO_HW's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param recv_map_out method that will be called with the payloads to be sent
   *        on the outgoing event data port 'recv_map_out'.
   * @param send_status_out method that will be called with the payloads to be sent
   *        on the outgoing event data port 'send_status_out'.
   */
  def check_concrete_output(recv_map_out: ISZ[SW.Command_Impl] => B = recv_map_outParam => {T},
                            send_status_out: ISZ[MC.MISSING_TYPE_Impl] => B = send_status_outParam => {T}): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    var recv_map_outValue: ISZ[SW.Command_Impl] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_recv_map_out().nonEmpty) recv_map_outValue = recv_map_outValue :+ get_recv_map_out().get
    if(!recv_map_out(recv_map_outValue)) {
      testFailures = testFailures :+ st"'recv_map_out' did not match expected: received ${recv_map_outValue.size} events with the following payloads ${recv_map_outValue}"
    }
    var send_status_outValue: ISZ[MC.MISSING_TYPE_Impl] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_send_status_out().nonEmpty) send_status_outValue = send_status_outValue :+ get_send_status_out().get
    if(!send_status_out(send_status_outValue)) {
      testFailures = testFailures :+ st"'send_status_out' did not match expected: received ${send_status_outValue.size} events with the following payloads ${send_status_outValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // setter for in EventDataPort
  def put_recv_map_inn(value : MC.MISSING_TYPE_Impl): Unit = {
    ArtNative_Ext.insertInPortValue(bridge.operational_api.recv_map_inn_Id, MC.MISSING_TYPE_Impl_Payload(value))
  }

  // setter for in EventDataPort
  def put_send_status_inn(value : SW.Coordinate_Impl): Unit = {
    ArtNative_Ext.insertInPortValue(bridge.operational_api.send_status_inn_Id, SW.Coordinate_Impl_Payload(value))
  }

  // getter for out EventDataPort
  def get_recv_map_out(): Option[SW.Command_Impl] = {
    val value: Option[SW.Command_Impl] = get_recv_map_out_payload() match {
      case Some(SW.Command_Impl_Payload(v)) => Some(v)
      case Some(v) => fail(s"Unexpected payload on port recv_map_out.  Expecting 'SW.Command_Impl_Payload' but received ${v}")
      case _ => None[SW.Command_Impl]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_recv_map_out_payload(): Option[SW.Command_Impl_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.recv_map_out_Id).asInstanceOf[Option[SW.Command_Impl_Payload]]
  }

  // getter for out EventDataPort
  def get_send_status_out(): Option[MC.MISSING_TYPE_Impl] = {
    val value: Option[MC.MISSING_TYPE_Impl] = get_send_status_out_payload() match {
      case Some(MC.MISSING_TYPE_Impl_Payload(v)) => Some(v)
      case Some(v) => fail(s"Unexpected payload on port send_status_out.  Expecting 'MC.MISSING_TYPE_Impl_Payload' but received ${v}")
      case _ => None[MC.MISSING_TYPE_Impl]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_send_status_out_payload(): Option[MC.MISSING_TYPE_Impl_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.send_status_out_Id).asInstanceOf[Option[MC.MISSING_TYPE_Impl_Payload]]
  }

}

