   *   ART currently supports single element event data queues so
   *   only the last element of mission_window will be used
   */
  def put_concrete_inputs(mission_window : ISZ[SW.MissionWindow]): Unit = {
    for(v <- mission_window){
      put_mission_window(v)
    }
  }


  /** helper function to check UARTDriver_Impl_MCMP_PROC_SW_UART's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param tracking_id method that will be called with the payloads to be sent
   *        on the outgoing event data port 'tracking_id'.
   */
  def check_concrete_output(tracking_id: ISZ[Base_Types.Integer_64] => B = tracking_idParam => {T}): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    var tracking_idValue: ISZ[Base_Types.Integer_64] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_tracking_id().nonEmpty) tracking_idValue = tracking_idValue :+ get_tracking_id().get
    if(!tracking_id(tracking_idValue)) {
      testFailures = testFailures :+ st"'tracking_id' did not match expected: received ${tracking_idValue.size} events with the following payloads ${tracking_idValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // setter for in EventDataPort
  def put_mission_window(value : SW.MissionWindow): Unit = {
    ArtNative_Ext.insertInPortValue(bridge.operational_api.mission_window_Id, SW.MissionWindow_Payload(value))
  }

  // getter for out EventDataPort
  def get_tracking_id(): Option[Base_Types.Integer_64] = {
    val value: Option[Base_Types.Integer_64] = get_tracking_id_payload() match {
      case Some(Base_Types.Integer_64_Payload(v)) => Some(v)
      case Some(v) => fail(s"Unexpected payload on port tracking_id.  Expecting 'Base_Types.Integer_64_Payload' but received ${v}")
      case _ => None[Base_Types.Integer_64]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_tracking_id_payload(): Option[Base_Types.Integer_64_Payload] = {
    return ArtNative_Ext.observeOutPortValue(bridge.initialization_api.tracking_id_Id).asInstanceOf[Option[Base_Types.Integer_64_Payload]]
  }

}

