/************************************************************************
 * sb_input_notification_handler:
 * Invoked by: seL4 notification callback
 *
 * This is the function invoked by an seL4 notification callback to 
 * dispatch the component due to the arrival of an event on port
 * input
 *
 ************************************************************************/
static void sb_input_notification_handler(void * unused) {
  MUTEXOP(sb_dispatch_sem_post())
  CALLBACKOP(sb_input_notification_reg_callback(sb_input_notification_handler, NULL));
}

/************************************************************************
 * sb_entrypoint_Comp_B_Impl_input:
 *
 * This is the function invoked by an active thread dispatcher to
 * call to a user-defined entrypoint function.  It sets up the dispatch
 * context for the user-defined entrypoint, then calls it.
 *
 ************************************************************************/
void sb_entrypoint_Comp_B_Impl_input(const int32_t * in_arg) {
  Comp_B_input((int32_t *) in_arg);
}

void post_init(void){
  // register callback for EventDataPort port input
  CALLBACKOP(sb_input_notification_reg_callback(sb_input_notification_handler, NULL));
}


/************************************************************************
 * int run(void)
 * Main active thread function.
 ************************************************************************/
int run(void) {
  int32_t sb_input;
  MUTEXOP(sb_dispatch_sem_wait())
  for(;;) {
    MUTEXOP(sb_dispatch_sem_wait())
    while (sb_input_dequeue((int32_t *) &sb_input)) {
      sb_entrypoint_Comp_B_Impl_input(&sb_input);
    }
  }
  return 0;
}

