procedure sb_Monitor_int8_t {
  include <sb_types.h>;
  bool is_empty();
  bool read(out int8_t m);
  bool write(refin int8_t m);
};

#include <sb_types.h>
#include "../includes/sb_destination_thread_component_read_port_Monitor.h"

int monsig_emit(void);

static int8_t contents;
bool receivedData = false;

bool mon_is_empty() {
  return !receivedData;
}

bool mon_read(int8_t * m) {
  *m = contents;
  return receivedData;
}

bool mon_write(const int8_t * m) {
  receivedData = true;
  contents = *m;
  monsig_emit();
  return receivedData;
}

