    connection seL4RPCCall conn2(from dest.sb_deq, to sb_dest_deq_monitor.mon);
    connection seL4Notification conn3(from sb_dest_deq_monitor.monsig, to dest.sb_deq_notification);
  }

  configuration {
  }
}

