#include <camkes.h>
#include <stdio.h>
#include <sb_proc_types.h>
#include <sb_source_t_impl.h>

static int8_t _value;

void testdpmon_source_component_init(const int64_t *in_arg)
{
    printf("testdpmon_source_component_init called\n");
    _value = 0;
}

/* control thread: keep calling enqueue for thing
 */
void run_sender(const int64_t *in_arg)
{
    if (sb_write_port_write( &_value ) ) {
        printf("[source] Sent %d\n", _value );
        _value = (_value + 1) % 500;
    }
}

/* testpdmon/components/destination/src/destination.c */

#include <camkes.h>
#include <stdio.h>
#include <sb_proc_types.h>
#include <sb_destination_t_impl.h>

void testdpmon_destination_component_init(const int64_t *in_arg)
{
    printf("testdpmon_destination_component_init called\n");
}


/* Handle monsig notification: there is QueuedData
 */
void run_receiver(const int64_t *in_arg)
{
    /* keep dequeuing until no more things can be had
     */
    int8_t value;

    if(sb_read_port_read(&value)){
        printf("[destination] value {%d}\n", value);
    }
}

procedure sb_Monitor_int8_t {
  include <sb_proc_types.h>;
  bool is_empty();
  bool read(out int8_t m);
  bool write(refin int8_t m);
};

#include <sb_proc_types.h>
#include "../includes/sb_dest_read_port_Monitor.h"

int monsig_emit(void);

static int8_t contents;
bool receivedData = false;

bool mon_is_empty() {
  return !receivedData;
}

bool mon_read(int8_t * m) {
  *m = contents;
  return receivedData;
}

bool mon_write(const int8_t * m) {
  receivedData = true;
  contents = *m;
  monsig_emit();
  return receivedData;
}

#include "../includes/sb_source_t_impl.h"
#include <string.h>
