component emitter_impl {
  include <sb_proc_types.h>;
  control;
  emits Notification sb_e;
  has semaphore sb_dispatch_sem;
}

component consumer_impl {
  include <sb_proc_types.h>;
  control;
  consumes Notification sb_s;
  has semaphore sb_dispatch_sem;
}

import <std_connector.camkes>;
import "components/emitter_impl/emitter_impl.camkes";
import "components/consumer_impl/consumer_impl.camkes";

assembly {
  composition {
    component emitter_impl src;
    component consumer_impl snk;

    connection seL4Notification conn1(from src.sb_e, to snk.sb_s);
  }

  configuration {

  }
}

/* testevent/components/emitter/src/emitter.c */

#include <camkes.h>
#include <stdio.h>
#include <sb_proc_types.h>
#include <sb_emitter_impl.h>

void testevent_emitter_component_init(const int64_t *in_arg)
{
    printf("testevent_emitter_component_init called\n");
}

/* control thread: keep calling enqueue for thing
 */
void run_emitter(const int64_t *in_arg)
{
    sb_emitter_impl_write_e();
    printf("[Emitter] Sent event.\n");
}

/* apps/testevent/components/Consumer/src/main.c */

/* Similar to the "helloevent" example in CAmkES doc,
 * tidied up a bit.
 */

#include <camkes.h>
#include <stdio.h>
#include <sb_consumer_impl.h>

static int8_t _value;

void testevent_consumer_component_init(const int64_t *in_arg) {
  printf("testevent_consumer_component_init called\n");
  _value = 0;
}

void testevent_consumer_s_event_handler() {

  printf("[Consumer] Callback %d fired.\n", _value);
  _value = (_value + 1) % 500;
}

#include "../includes/sb_emitter_impl.h"
#include <string.h>
#include <camkes.h>

/************************************************************************
 * sb_emitter_impl_write_e
 * Invoked from user code in the local thread.
 *
 * This is the function invoked by the local thread to make a
 * call to write to a remote data port.
 *
 ************************************************************************/
bool sb_emitter_impl_write_e(void) {
  bool sb_result = true;
  sb_e_emit();
  return sb_result;
}


/************************************************************************
 *  sb_entrypoint_emitter_impl_initializer:
 *
 * This is the function invoked by an active thread dispatcher to
 * call to a user-defined entrypoint function.  It sets up the dispatch
 * context for the user-defined entrypoint, then calls it.
 *
 ************************************************************************/
void sb_entrypoint_emitter_impl_initializer(const int64_t * in_arg) {
  testevent_emitter_component_init((int64_t *) in_arg);
}

void pre_init(void) {
}

/************************************************************************
 * int run(void)
 * Main active thread function.
 ************************************************************************/
int run(void) {
  {
    int64_t sb_dummy;
    sb_entrypoint_emitter_impl_initializer(&sb_dummy);
  }

  for(;;) {
    MUTEXOP(sb_dispatch_sem_wait())

  }
  return 0;
}

#include "../includes/sb_consumer_impl.h"
#include <string.h>
#include <camkes.h>

/************************************************************************
 *
 * Static variables and queue management functions for event port:
 *     s
 *
 ************************************************************************/
static bool sb_s_index = false;

/************************************************************************
 * sb_s_handler:
 * Invoked by: remote RPC
 *
 * This is the function invoked by a remote RPC to write to an active-thread
 * input event port.  It increments a count of received messages.
 *
 ************************************************************************/
static void sb_s_handler(void *_ UNUSED){
  sb_s_index = true;
  MUTEXOP(sb_dispatch_sem_post());
  CALLBACKOP(sb_s_reg_callback(sb_s_handler, NULL));
}

/************************************************************************
 * sb_consumer_impl_read_s:
 * Invoked from local active thread.
 *
 * This is the function invoked by the active thread to decrement the
 * input event index.
 *
 ************************************************************************/
bool sb_consumer_impl_read_s(){
  bool result;
  result = sb_s_index;
  sb_s_index = false;
  return result;
}

/************************************************************************
 *  sb_entrypointsb_consumer_impl_s
 *
 * This is the function invoked by an active thread dispatcher to
 * call to a user-defined entrypoint function.  It sets up the dispatch
 * context for the user-defined entrypoint, then calls it.
 *
 ************************************************************************/
void sb_entrypointsb_consumer_impl_s(void){
  testevent_consumer_s_event_handler();
}

/************************************************************************
 *  sb_entrypoint_consumer_impl_initializer:
 *
 * This is the function invoked by an active thread dispatcher to
 * call to a user-defined entrypoint function.  It sets up the dispatch
 * context for the user-defined entrypoint, then calls it.
 *
 ************************************************************************/
void sb_entrypoint_consumer_impl_initializer(const int64_t * in_arg) {
  testevent_consumer_component_init((int64_t *) in_arg);
}

void pre_init(void) {
  CALLBACKOP(sb_s_reg_callback(sb_s_handler, NULL));
}

/************************************************************************
 * int run(void)
 * Main active thread function.
 ************************************************************************/
int run(void) {

  {
    int64_t sb_dummy;
    sb_entrypoint_consumer_impl_initializer(&sb_dummy);
  }

  for(;;) {
    MUTEXOP(sb_dispatch_sem_wait())

    if(sb_consumer_impl_read_s()){
      sb_entrypointsb_consumer_impl_s();
    }
  }
  return 0;
}

