import "../../interfaces/AADLEvent_Sender.idl4";
import <std_connector.camkes>;

component emitter_impl {
  include <sb_proc_types.h>;
  control;
  uses AADLEvent_Sender sb_e0;
  uses Timer sb_timer;
  consumes Notification sb_periodic_dispatch_notification;
  has semaphore sb_dispatch_sem;
}

import "../../interfaces/AADLEvent_Receiver.idl4";

component consumer_impl {
  include <sb_proc_types.h>;
  control;
  uses AADLEvent_Receiver sb_s;
  consumes ReceiveEvent sb_s_notification;
  has semaphore sb_dispatch_sem;
}

import "../../../interfaces/AADLEvent_Receiver.idl4";
import "../../../interfaces/AADLEvent_Sender.idl4";

component sb_snk_s_Monitor {

  provides AADLEvent_Receiver mon_receive;
  provides AADLEvent_Sender mon_send;
  emits ReceiveEvent monsig;
  has mutex m;
}

import <global-connectors.camkes>;

component dispatch_periodic {
  control;
  uses Timer timer;
  emits Notification sb_src_periodic_dispatch_notification;
  consumes Notification timer_complete;
}

import <std_connector.camkes>;
import <global-connectors.camkes>;
import <TimeServer/TimeServer.camkes>;;
import "components/emitter_impl/emitter_impl.camkes";
import "components/consumer_impl/consumer_impl.camkes";
import "components/sb_Monitors/sb_snk_s_Monitor/sb_snk_s_Monitor.camkes";
import "components/dispatch_periodic/dispatch_periodic.camkes";

assembly {
  composition {
    component emitter_impl src;
    component consumer_impl snk;
    component sb_snk_s_Monitor sb_snk_s_monitor;
    component dispatch_periodic dispatch_periodic_inst;
    component TimeServer time_server;

    connection seL4TimeServer conn1(from src.sb_timer, to time_server.the_timer);
    connection seL4Notification conn2(from dispatch_periodic_inst.sb_src_periodic_dispatch_notification, to src.sb_periodic_dispatch_notification);
    connection seL4RPCCall conn3(from src.sb_e0, to sb_snk_s_monitor.mon_send);
    connection seL4RPCCall conn4(from snk.sb_s, to sb_snk_s_monitor.mon_receive);
    connection seL4Notification conn5(from sb_snk_s_monitor.monsig, to snk.sb_s_notification);
    connection seL4TimeServer conn6(from dispatch_periodic_inst.timer, to time_server.the_timer);
    connection seL4GlobalAsynchCallback conn7(from time_server.timer_notification, to dispatch_periodic_inst.timer_complete);
  }

  configuration {
    src.sb_timer_attributes = 1;
    src.sb_timer_global_endpoint = "emitter_impl_sb_timer";
    time_server.timers_per_client = 1;
    dispatch_periodic_inst.timer_attributes = 2;
    dispatch_periodic_inst.timer_global_endpoint = "dispatch_periodic_timer";
    dispatch_periodic_inst.timer_complete_global_endpoint = "dispatch_periodic_timer";
    dispatch_periodic_inst.priority = 201;
  }
}

/* testevent/components/emitter/src/emitter.c */

#include <camkes.h>
#include <stdio.h>
#include <sb_proc_types.h>
#include <sb_emitter_impl.h>

static int32_t counter = 0;

void testevent_emitter_component_init(const int64_t *in_arg)
{
    printf("testevent_emitter_component_init called\n");
}

/* control thread: keep calling enqueue for thing
 */
void run_emitter(const int64_t *in_arg){
  int numEvents = counter % 7; // send 0 - 6 events per dispatch, consumer's queue size is 5
  for(int32_t i = 0; i < numEvents; i++) {
    sb_emitter_impl_write_e();
  }
  printf("[Emitter] Sent %i events.\n", numEvents);

  counter++;
}

