import org.sireum._
import art.Art
import art.scheduling.Scheduler

@record class Legacy(bridges: IS[Art.BridgeId, art.Bridge]) extends Scheduler {

  override def initialize(): Unit = { }

  override def initializationPhase(): Unit = {
    for (bridge <- bridges) {
      bridge.entryPoints.initialise()
      Art.logInfo(bridge.id, s"Initialized bridge: ${bridge.name}")
    }
  }

  override def computePhase(): Unit = {
    LegacyInterface.computePhase(bridges)
  }

  override def finalizePhase(): Unit = {
    for (bridge <- bridges) {
      bridge.entryPoints.finalise()
      Art.logInfo(bridge.id, s"Finalized bridge: ${bridge.name}")
    }
  }
}

@ext object LegacyInterface {
  def computePhase(bridges: IS[Art.BridgeId, art.Bridge]): Unit = $
}

package art.scheduling.legacy

import art.{Art, ArtNative, ArtNative_Ext, DispatchPropertyProtocol}
import scala.collection.mutable.{Map => MMap}
import org.sireum._

object LegacyInterface_Ext {
  val slowdown: Z = 1

  def computePhase(bridges: IS[Art.BridgeId, art.Bridge]): Unit = {
    var terminated = false
    var numTerminated: Z = 0

    for (bridge <- bridges) {

      val (rate, isSporadic) = bridge.dispatchProtocol match {
        case DispatchPropertyProtocol.Periodic(period) => (period, F)
        case DispatchPropertyProtocol.Sporadic(min) => (min, T)
      }

      new Thread(() => {
        ArtNative.logInfo(Art.logTitle, s"Thread for ${bridge.name} instantiated.")
        ArtNative_Ext.synchronized {
          ArtNative_Ext.wait()
        }
        while (!terminated) {
          Thread.sleep((rate * slowdown).toMP.toLong)
          if (ArtNative.shouldDispatch(bridge.id)) {
            try {
              bridge.synchronized {
                bridge.entryPoints.compute()
              }
            }
            catch {
              case x : Throwable =>
                x.printStackTrace()
                terminated = true
            }
          }
        }
        ArtNative_Ext.synchronized {
          numTerminated += 1
        }
      }).start()
    }

    Thread.sleep(1000)

    ArtNative.logInfo(Art.logTitle, s"Start execution (press Enter twice to terminate) ...")

    ArtNative_Ext.synchronized {
      ArtNative_Ext.notifyAll()
    }

    Console.in.readLine()
    terminated = true

    while (numTerminated != bridges.size) {
      Thread.sleep(1000)
    }
  }

  def concMap[K, V](): MMap[K, V] = {
    import org.sireum.$internal.CollectionCompat.Converters._
    new java.util.concurrent.ConcurrentHashMap[K, V].asInstanceOf[java.util.Map[K, V]].asScala
  }
}

