import <std_connector.camkes>;
component emitter_impl_testevent_src {
  include <sb_types.h>;
  include <sb_event_counter.h>;
  control;
  emits ReceiveEvent sb_e;
  consumes Notification sb_periodic_dispatch_notification;
  dataport sb_event_counter_t sb_e_counter;
  has semaphore sb_dispatch_sem;
}

component consumer_impl_testevent_snk {
  include <sb_types.h>;
  include <sb_event_counter.h>;
  control;
  consumes ReceiveEvent sb_s;
  dataport sb_event_counter_t sb_s_counter;
  has semaphore sb_dispatch_sem;
}

import <global-connectors.camkes>;
component dispatch_periodic {
  control;
  uses Timer timer;
  emits Notification sb_testevent_src_periodic_dispatch_notification;
  consumes Notification timer_complete;
}

