import <std_connector.camkes>;
import <global-connectors.camkes>;
import <TimeServer/TimeServer.camkes>;;
import "components/emitter_impl_testevent_src/emitter_impl_testevent_src.camkes";
import "components/consumer_impl_testevent_snk/consumer_impl_testevent_snk.camkes";
import "components/dispatch_periodic/dispatch_periodic.camkes";

assembly {
  composition {
    component emitter_impl_testevent_src testevent_src;
    component consumer_impl_testevent_snk testevent_snk;
    component dispatch_periodic dispatch_periodic_inst;
    component TimeServer time_server;

    connection seL4Notification conn1(from testevent_src.sb_e, to testevent_snk.sb_s);
    connection seL4SharedData conn2(from testevent_src.sb_e_counter, to testevent_snk.sb_s_counter);
    connection seL4Notification conn3(from dispatch_periodic_inst.sb_testevent_src_periodic_dispatch_notification, to testevent_src.sb_periodic_dispatch_notification);
    connection seL4TimeServer conn4(from dispatch_periodic_inst.timer, to time_server.the_timer);
    connection seL4GlobalAsynchCallback conn5(from time_server.timer_notification, to dispatch_periodic_inst.timer_complete);
  }

  configuration {
    testevent_src.sb_e_counter_access = "W";
    testevent_snk.sb_s_counter_access = "R";
    time_server.timers_per_client = 1;
    dispatch_periodic_inst.timer_attributes = 1;
    dispatch_periodic_inst.priority = 201;
  }
}

/* testevent/components/emitter/src/emitter.c */

#include <camkes.h>
#include <stdio.h>
#include <sb_types.h>
#include <sb_emitter_impl.h>

static int32_t counter = 0;

void testevent_emitter_component_init(const int64_t *in_arg)
{
    printf("testevent_emitter_component_init called\n");
}

/* control thread: keep calling enqueue for thing
 */
void run_emitter(const int64_t *in_arg){
  int numEvents = counter % 7; // send 0 - 6 events per dispatch, consumer's queue size is 5
  for(int32_t i = 0; i < numEvents; i++) {
    sb_e_enqueue();
  }
  printf("[Emitter] Sent %i events.\n", numEvents);

  counter++;
}

/* apps/testevent/components/Consumer/src/main.c */

/* Similar to the "helloevent" example in CAmkES doc,
 * tidied up a bit.
 */

#include <camkes.h>
#include <stdio.h>
#include <sb_consumer_impl.h>

void testevent_consumer_component_init(const int64_t *in_arg) {
  printf("testevent_consumer_component_init called\n");
}

void testevent_consumer_s_event_handler() {
  int32_t receivedEvents = 1; // 1 for the event that caused handler to be invoked
  while(sb_s_dequeue()) {
    receivedEvents++;
  }

  printf("[Consumer] received %i events\n\n", receivedEvents);
}

