object Map {
  // Import I's interpolator to create instances of I.  For e.g.,
  //   import SW.Map.I._
  //   object Example {
  //     val value: SW.Map.I = i"0"
  //     ...
  //
  // Use the F7DD40 method when using multiple <array-def>.I indexing types in the same
  // context.  Alternatively, rename I and use its fromZ method when using multiple
  //   import SW.Map.{I => I0}
  //   import <other-array-def>.{I => I1}
  //   object Example {
  //     val value: SW.Map.I = I0.fromZ(0)
  //     ...

  @range(min = 0, max = 3, index = T) class I

  @pure def F7DD40(z: Z): I = {
    Contract(
      Requires(I.Min.toZ <= z && z <= I.Max.toZ),
      Ensures(Res[I].toZ == z)
    )
    return I.fromZ(z)
  }

  def example(): SW.Map = {
    return SW.Map(
      value = IS.create[I, SW.Coordinate_Impl](4, SW.Coordinate_Impl.example()))
  }
}

