@msig trait Radio_Impl_MCMP_RADIO_HW_TestApi {

  def BeforeEntrypoint(): Unit = {
    Art.initTest(Arch.UAV_Impl_Instance_MCMP_RADIO_HW)
  }

  def AfterEntrypoint(): Unit = {
    Art.finalizeTest(Arch.UAV_Impl_Instance_MCMP_RADIO_HW)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.UAV_Impl_Instance_MCMP_RADIO_HW)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.UAV_Impl_Instance_MCMP_RADIO_HW)
  }

  /** helper function to set the values of all input ports.
   * @param recv_map_inn payloads for event data port recv_map_inn.
   *   ART currently supports single element event data queues so
   *   only the last element of recv_map_inn will be used
   * @param send_status_inn payloads for event data port send_status_inn.
   *   ART currently supports single element event data queues so
