    Art.finalizeTest(Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM)
  }

  /** helper function to set the values of all input ports.
   * @param flight_plan payloads for event data port flight_plan.
   *   ART currently supports single element event data queues so
   *   only the last element of flight_plan will be used
   * @param tracking_id payloads for event data port tracking_id.
   *   ART currently supports single element event data queues so
   *   only the last element of tracking_id will be used
   */
  def put_concrete_inputs(flight_plan : ISZ[SW.Mission],
                          tracking_id : ISZ[Base_Types.Integer_64]): Unit = {
    for(v <- flight_plan){
      put_flight_plan(v)
    }
    for(v <- tracking_id){
      put_tracking_id(v)
    }
  }


  /** helper function to check WaypointManager_Impl_MCMP_PROC_SW_WPM's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param mission_rcv method that will be called with the payloads to be sent
   *        on the outgoing event data port 'mission_rcv'.
   * @param mission_window method that will be called with the payloads to be sent
   *        on the outgoing event data port 'mission_window'.
   */
  def check_concrete_output(mission_rcv: ISZ[Base_Types.Boolean] => B,
                            mission_window: ISZ[SW.MissionWindow] => B): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    var mission_rcvValue: ISZ[Base_Types.Boolean] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_mission_rcv().nonEmpty) { mission_rcvValue = mission_rcvValue :+ get_mission_rcv().get }
    if(!mission_rcv(mission_rcvValue)) {
      testFailures = testFailures :+ st"'mission_rcv' did not match expected: received ${mission_rcvValue.size} events with the following payloads ${mission_rcvValue}"
    }
    var mission_windowValue: ISZ[SW.MissionWindow] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_mission_window().nonEmpty) { mission_windowValue = mission_windowValue :+ get_mission_window().get }
    if(!mission_window(mission_windowValue)) {
      testFailures = testFailures :+ st"'mission_window' did not match expected: received ${mission_windowValue.size} events with the following payloads ${mission_windowValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // setter for in EventDataPort
  def put_flight_plan(value : SW.Mission): Unit = {
    Art.insertInInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM.operational_api.flight_plan_Id, SW.Mission_Payload(value))
  }

  // setter for in EventDataPort
  def put_tracking_id(value : Base_Types.Integer_64): Unit = {
    Art.insertInInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM.operational_api.tracking_id_Id, Base_Types.Integer_64_Payload(value))
  }

  // getter for out EventDataPort
  def get_mission_rcv(): Option[Base_Types.Boolean] = {
    val value: Option[Base_Types.Boolean] = get_mission_rcv_payload() match {
      case Some(Base_Types.Boolean_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port mission_rcv.  Expecting 'Base_Types.Boolean_Payload' but received ${v}")
      case _ => None[Base_Types.Boolean]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_mission_rcv_payload(): Option[Base_Types.Boolean_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM.initialization_api.mission_rcv_Id).asInstanceOf[Option[Base_Types.Boolean_Payload]]
  }

  // getter for out EventDataPort
  def get_mission_window(): Option[SW.MissionWindow] = {
    val value: Option[SW.MissionWindow] = get_mission_window_payload() match {
      case Some(SW.MissionWindow_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port mission_window.  Expecting 'SW.MissionWindow_Payload' but received ${v}")
      case _ => None[SW.MissionWindow]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_mission_window_payload(): Option[SW.MissionWindow_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM.initialization_api.mission_window_Id).asInstanceOf[Option[SW.MissionWindow_Payload]]
  }

}

