@sig trait WaypointManager_Impl_Api {
  def id: Art.BridgeId
  def flight_plan_Id : Art.PortId
  def mission_rcv_Id : Art.PortId
  def mission_window_Id : Art.PortId
  def tracking_id_Id : Art.PortId

  // Logika spec var representing port state for outgoing event data port
  @spec var mission_rcv: Option[Base_Types.Boolean] = $

  def put_mission_rcv(value : Base_Types.Boolean) : Unit = {
    Contract(
      Modifies(mission_rcv),
      Ensures(
        mission_rcv == Some(value)
      )
    )
    Spec {
      mission_rcv = Some(value)
    }

    Art.putValue(mission_rcv_Id, Base_Types.Boolean_Payload(value))
  }

  // Logika spec var representing port state for outgoing event data port
  @spec var mission_window: Option[SW.MissionWindow] = $

  def put_mission_window(value : SW.MissionWindow) : Unit = {
    Contract(
      Modifies(mission_window),
      Ensures(
        mission_window == Some(value)
      )
    )
    Spec {
      mission_window = Some(value)
    }

    Art.putValue(mission_window_Id, SW.MissionWindow_Payload(value))
  }

  def logInfo(msg: String): Unit = {
    Art.logInfo(id, msg)
  }

  def logDebug(msg: String): Unit = {
    Art.logDebug(id, msg)
  }

  def logError(msg: String): Unit = {
    Art.logError(id, msg)
  }
}

@datatype class WaypointManager_Impl_Initialization_Api (
  val id: Art.BridgeId,
  val flight_plan_Id : Art.PortId,
  val mission_rcv_Id : Art.PortId,
  val mission_window_Id : Art.PortId,
  val tracking_id_Id : Art.PortId) extends WaypointManager_Impl_Api

@datatype class WaypointManager_Impl_Operational_Api (
  val id: Art.BridgeId,
  val flight_plan_Id : Art.PortId,
  val mission_rcv_Id : Art.PortId,
  val mission_window_Id : Art.PortId,
  val tracking_id_Id : Art.PortId) extends WaypointManager_Impl_Api {

  // Logika spec var representing port state for incoming event data port
  @spec var flight_plan: Option[SW.Mission] = $

  def get_flight_plan() : Option[SW.Mission] = {
    Contract(
      Ensures(
        Res == flight_plan
      )
    )
    val value : Option[SW.Mission] = Art.getValue(flight_plan_Id) match {
      case Some(SW.Mission_Payload(v)) => Some(v)
      case Some(v) =>
        Art.logError(id, s"Unexpected payload on port flight_plan.  Expecting 'SW.Mission_Payload' but received ${v}")
        None[SW.Mission]()
      case _ => None[SW.Mission]()
    }
    return value
  }

  // Logika spec var representing port state for incoming event data port
  @spec var tracking_id: Option[Base_Types.Integer_64] = $

  def get_tracking_id() : Option[Base_Types.Integer_64] = {
    Contract(
      Ensures(
        Res == tracking_id
      )
    )
    val value : Option[Base_Types.Integer_64] = Art.getValue(tracking_id_Id) match {
      case Some(Base_Types.Integer_64_Payload(v)) => Some(v)
      case Some(v) =>
        Art.logError(id, s"Unexpected payload on port tracking_id.  Expecting 'Base_Types.Integer_64_Payload' but received ${v}")
        None[Base_Types.Integer_64]()
      case _ => None[Base_Types.Integer_64]()
    }
    return value
  }
}

