}

@datatype class UrgentPort[T](val id: Art.PortId,
                              val name: String,
                              val mode: PortMode.Type,
                              val urgency: Z)
  extends UrgentPortProto

@sig trait Bridge {
  def id: Art.BridgeId

  def name: String

  def ports: Bridge.Ports

  def entryPoints: Bridge.EntryPoints

