import <std_connector.camkes>;
import "components/RadioDriver_Impl_MCMP_PROC_SW_RADIO/RadioDriver_Impl_MCMP_PROC_SW_RADIO.camkes";
import "components/FlightPlanner_Impl_MCMP_PROC_SW_FPLN/FlightPlanner_Impl_MCMP_PROC_SW_FPLN.camkes";
import "components/WaypointManager_Impl_MCMP_PROC_SW_WPM/WaypointManager_Impl_MCMP_PROC_SW_WPM.camkes";
import "components/UARTDriver_Impl_MCMP_PROC_SW_UART/UARTDriver_Impl_MCMP_PROC_SW_UART.camkes";

assembly {
  composition {
    component RadioDriver_Impl_MCMP_PROC_SW_RADIO MCMP_PROC_SW_RADIO;
    component FlightPlanner_Impl_MCMP_PROC_SW_FPLN MCMP_PROC_SW_FPLN;
    component WaypointManager_Impl_MCMP_PROC_SW_WPM MCMP_PROC_SW_WPM;
    component UARTDriver_Impl_MCMP_PROC_SW_UART MCMP_PROC_SW_UART;

    connection seL4Notification conn1(from MCMP_PROC_SW_RADIO.sb_recv_map_out_1_notification, to MCMP_PROC_SW_FPLN.sb_recv_map_notification);
    connection seL4SharedData conn2(from MCMP_PROC_SW_RADIO.sb_recv_map_out_queue_1, to MCMP_PROC_SW_FPLN.sb_recv_map_queue);
    connection seL4Notification conn3(from MCMP_PROC_SW_FPLN.sb_flight_plan_1_notification, to MCMP_PROC_SW_WPM.sb_flight_plan_notification);
    connection seL4SharedData conn4(from MCMP_PROC_SW_FPLN.sb_flight_plan_queue_1, to MCMP_PROC_SW_WPM.sb_flight_plan_queue);
    connection seL4Notification conn5(from MCMP_PROC_SW_WPM.sb_mission_rcv_1_notification, to MCMP_PROC_SW_FPLN.sb_mission_rcv_notification);
    connection seL4SharedData conn6(from MCMP_PROC_SW_WPM.sb_mission_rcv_queue_1, to MCMP_PROC_SW_FPLN.sb_mission_rcv_queue);
    connection seL4Notification conn7(from MCMP_PROC_SW_WPM.sb_mission_window_1_notification, to MCMP_PROC_SW_UART.sb_mission_window_notification);
    connection seL4SharedData conn8(from MCMP_PROC_SW_WPM.sb_mission_window_queue_1, to MCMP_PROC_SW_UART.sb_mission_window_queue);
    connection seL4Notification conn9(from MCMP_PROC_SW_UART.sb_tracking_id_1_notification, to MCMP_PROC_SW_WPM.sb_tracking_id_notification);
    connection seL4SharedData conn10(from MCMP_PROC_SW_UART.sb_tracking_id_queue_1, to MCMP_PROC_SW_WPM.sb_tracking_id_queue);
  }

  configuration {
    MCMP_PROC_SW_RADIO.sb_recv_map_out_queue_1_access = "W";
    MCMP_PROC_SW_FPLN.sb_recv_map_queue_access = "R";
    MCMP_PROC_SW_FPLN.sb_flight_plan_queue_1_access = "W";
    MCMP_PROC_SW_WPM.sb_flight_plan_queue_access = "R";
    MCMP_PROC_SW_WPM.sb_mission_rcv_queue_1_access = "W";
    MCMP_PROC_SW_FPLN.sb_mission_rcv_queue_access = "R";
    MCMP_PROC_SW_WPM.sb_mission_window_queue_1_access = "W";
    MCMP_PROC_SW_UART.sb_mission_window_queue_access = "R";
    MCMP_PROC_SW_UART.sb_tracking_id_queue_1_access = "W";
    MCMP_PROC_SW_WPM.sb_tracking_id_queue_access = "R";
  }
}

#include <camkes.h>
#include <sb_types.h>
#include <sb_RadioDriver_Impl.h>
#include <assert.h>

void init(const int64_t * in_arg) {

  printf("RDIO: initialise entry point called\n");

  SW__Command_Impl command = {
    .Map = {{ .lat = 0, .longitude = 1, .alt = 2 },
            { .lat = 1, .longitude = 2, .alt = 3 },
            { .lat = 2, .longitude = 3, .alt = 4 },
            { .lat = 3, .longitude = 4, .alt = 5 }},
      .Pattern = Perimeter,
      .HMAC = true
  };

  printf("RDIO:> Sending command.\n");

  // send command to FPLN  
  assert(sb_recv_map_out_enqueue(&command)==true);
}

#include <camkes.h>
#include <sb_types.h>
#include <sb_FlightPlanner_Impl.h>
#include <assert.h>

static bool missioncommand_sent = false;

void init(const int64_t *arg){
}

void printCommand(const SW__Command_Impl * command) {
  	printf("  Map     = [\n");
    for(int i = 0; i < 4; i++){
      SW__Coordinate_Impl c = command->Map[i];
      printf("             %i: {%i, %i, %i}\n", i, c.lat, c.longitude, c.alt);
    }  	
    printf("            ]\n");
		printf("  Pattern = %i\n", command->Pattern);
		printf("  HMAC    = %i\n", command->HMAC);
}

void recv_map(const SW__Command_Impl * command) {

  	printf("FPLN:< Command.\n");
  	printCommand(command);

  	if(!missioncommand_sent) {

  	    // simulate the creation of a new mission
  		sb_SW__Mission_container mission = { .f = {{ .lat = 0, .longitude = 1, .alt = 2},
   		                                           { .lat = 1, .longitude = 2, .alt = 3},
  		                                           { .lat = 2, .longitude = 3, .alt = 4},
  		                                           { .lat = 3, .longitude = 4, .alt = 5},
  		                                           { .lat = 4, .longitude = 5, .alt = 6},
  		                                           { .lat = 5, .longitude = 6, .alt = 7},
  		                                           { .lat = 6, .longitude = 7, .alt = 8},
  		                                           { .lat = 7, .longitude = 8, .alt = 9},
  		                                           { .lat = 8, .longitude = 9, .alt = 10},
  		                                           { .lat = 9, .longitude = 10, .alt = 11}}};

  		printf("FPLN:> new mission notification.\n");

  		// send the flight plan to WMP
  		assert(sb_flight_plan_enqueue(&mission) == true);

    	missioncommand_sent = true;
  	}
}

void mission_rcv(const bool * value) {
	printf("FPLN:< Received mission receipt confirmation: %i.\n", *value);
}

#include <camkes.h>
#include <sb_types.h>
#include <sb_WaypointManager_Impl.h>
#include <assert.h>
#include <string.h>


sb_SW__Mission_container _mission;

void init(const int64_t * __unused__) {
}

void printMission(const sb_SW__Mission_container * mission){
  printf("  Mission:\n");
  for(int i = 0; i < 10; i++){
    SW__Coordinate_Impl c = mission->f[i];
    printf("    %i: {%i, %i, %i}\n", i, c.lat, c.longitude, c.alt);
  }
}

void sendWin(int i) {

  // simulate creation of new mission windows
  sb_SW__MissionWindow_container missionWindow = { 
    .f = { _mission.f[i % 10], 
           _mission.f[(i + 1) % 10],
           _mission.f[(i + 2) % 10],
           _mission.f[(i + 3) % 10]} 
  };

  // send mission window to UART  
  assert(sb_mission_window_enqueue(&missionWindow) == true);

  printf("WM:> Sent mission window\n");
}

void flight_plan(const sb_SW__Mission_container * mission){
  printf("WM:< Received flight plan\n");

  memcpy(&_mission, mission, sizeof(struct sb_SW__Mission_container));

  printMission(&_mission);

  sendWin(0);

  // send receipt confirmation back to FPLN
  bool dummy = true;
  assert(sb_mission_rcv_enqueue(&dummy) == true);
}

void tracking_id(const int64_t * nid) {
  printf("WM:< Received %lu as the next id.\n", *nid);

  sendWin(*nid);
}

#include <camkes.h>
#include <sb_types.h>
#include <sb_UARTDriver_Impl.h>
#include <assert.h>

static int64_t i = 0;

void init(const int64_t * in_arg) {
}

void printMissionWindow(const sb_SW__MissionWindow_container * missionWindow){
  printf("  MissionWindow:\n");
  for(int i = 0; i < 4; i++){
    SW__Coordinate_Impl c = missionWindow->f[i];
    printf("    %i: {%i, %i, %i}\n", i, c.lat, c.longitude, c.alt);
  }
}

void mission_window(const sb_SW__MissionWindow_container * missionWindow){
  printf("UART:< Received mission window\n");
  printMissionWindow(missionWindow);

  for(unsigned int j = 0; j < 100000000; j++){}

  i++;

  printf("UART:> Sending %lu as the next id.\n", i);

  // send tracking id to WPM
  assert(sb_tracking_id_enqueue(&i) == true);
}

