@msig trait emitter_t_impl_src_process_src_thread_TestApi {

  def BeforeEntrypoint(): Unit = {
    Art.initTest(Arch.top_impl_Instance_src_process_src_thread)
  }

  def AfterEntrypoint(): Unit = {
    Art.finalizeTest(Arch.top_impl_Instance_src_process_src_thread)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.top_impl_Instance_src_process_src_thread)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.top_impl_Instance_src_process_src_thread)
  }

  /** helper function to check emitter_t_impl_src_process_src_thread's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param write_port method that will be called with the value of the outgoing data
   *        port 'write_port'.
   */
  def check_concrete_output(write_port: Base_Types.Integer_8 => B): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    val write_portValue: Base_Types.Integer_8 = get_write_port().get
    if(!write_port(write_portValue)) {
      testFailures = testFailures :+ st"'write_port' did not match expected: value of the outgoing data port is ${write_portValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // getter for out DataPort
  def get_write_port(): Option[Base_Types.Integer_8] = {
    val value: Option[Base_Types.Integer_8] = get_write_port_payload() match {
      case Some(Base_Types.Integer_8_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port write_port.  Expecting 'Base_Types.Integer_8_Payload' but received ${v}")
      case _ => None[Base_Types.Integer_8]()
    }
    return value
  }

  // payload getter for out DataPort
  def get_write_port_payload(): Option[Base_Types.Integer_8_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.top_impl_Instance_src_process_src_thread.initialization_api.write_port_Id).asInstanceOf[Option[Base_Types.Integer_8_Payload]]
  }

}

