package uav_june_step6__Linux

import org.sireum._
import art.Art
import art.scheduling.legacy.Legacy
import art.scheduling.roundrobin.RoundRobin
import art.scheduling.static.Schedule.{DSchedule, DScheduleSpec}
import art.scheduling.static._

// Do not edit this file as it will be overwritten if HAMR codegen is rerun

@datatype class ProcessorTimingProperties(val clockPeriod: Option[Z],
                                          val framePeriod: Option[Z],
                                          val maxDomain: Option[Z],
                                          val slotTime: Option[Z])

@datatype class ThreadTimingProperties(val domain: Option[Z],
                                       val computeExecutionTime: Option[(Z, Z)])

object Schedulers {

  val threadNickNames: Map[String, Art.BridgeId] = Map(
    ISZ(
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_FC_UART_UARTDriver.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_FC_UART_UARTDriver.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_RADIO_RadioDriver_Attestation.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_RADIO_RadioDriver_Attestation.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_FlyZones_FlyZonesDatabase.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_FlyZones_FlyZonesDatabase.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_UXAS_UxAS.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_UXAS_UxAS.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM_WaypointPlanManagerService.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM_WaypointPlanManagerService.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_AM_Gate_CASE_AttestationGate.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_AM_Gate_CASE_AttestationGate.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_FLT_LST_CASE_Filter_LST.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_FLT_LST_CASE_Filter_LST.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_REQ_CASE_Monitor_Req.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_REQ_CASE_Monitor_Req.id,
      Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo.name ~> Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo.id)
  )

  val revThreadNickNames: Map[Art.BridgeId, String] = Map.empty[Art.BridgeId, String] ++ (for (e <- threadNickNames.entries) yield e._2 ~> e._1)

  val UAV_Impl_Instance_MCMP_PROC_HW_timingProperties: ProcessorTimingProperties = ProcessorTimingProperties(
    clockPeriod = Some(2),
    framePeriod = Some(1000),
    maxDomain = Some(15),
    slotTime = None())

  val UAV_Impl_Instance_MCMP_PROC_SW_FC_UART_UARTDriver_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((6, 6)),
    domain = Some(3))

  val UAV_Impl_Instance_MCMP_PROC_SW_RADIO_RadioDriver_Attestation_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(2))

  val UAV_Impl_Instance_MCMP_PROC_SW_FlyZones_FlyZonesDatabase_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(10))

  val UAV_Impl_Instance_MCMP_PROC_SW_UXAS_UxAS_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(9))

  val UAV_Impl_Instance_MCMP_PROC_SW_WPM_WaypointPlanManagerService_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(14))

  val UAV_Impl_Instance_MCMP_PROC_SW_AM_Gate_CASE_AttestationGate_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(5))

  val UAV_Impl_Instance_MCMP_PROC_SW_FLT_LST_CASE_Filter_LST_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(7))

  val UAV_Impl_Instance_MCMP_PROC_SW_MON_REQ_CASE_Monitor_Req_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(11))

  val UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo_timingProperties: ThreadTimingProperties = ThreadTimingProperties(
    computeExecutionTime = Some((2, 2)),
    domain = Some(13))


  /**********************************************************************
   * Round Robin Scheduler
   *********************************************************************/

  // roundRobinSchedule represents the component dispatch order
  val roundRobinSchedule: ISZ[Art.BridgeId] = {
    // convert IS[Art.BridgeId, art.Bridge] to an IS[Z, Art.BridgeId] to allow bridges to be dispatched
    // multiple times during a hyper-period
    var ret: ISZ[Art.BridgeId] = ISZ()
    for (e <- Arch.ad.components) {
      ret = ret :+ e.id
    }
    ret
  }

  def getRoundRobinScheduler(schedule: Option[ISZ[Art.BridgeId]]): RoundRobin = {
    if (roundRobinSchedule.isEmpty) {} // line needed for transpiler; do not remove
    schedule match {
      case Some(s) => return RoundRobin(s)
      case _ => return RoundRobin(ScheduleProviderI.getRoundRobinOrder())
    }
  }

  /**********************************************************************
   * Static Scheduler
   *********************************************************************/

  val framePeriod: Z = 1000
  val numComponents: Z = Arch.ad.components.size
  val maxExecutionTime: Z = numComponents / framePeriod

  // defaultStaticSchedule represents the component dispatch order
  val defaultStaticSchedule: DScheduleSpec = DScheduleSpec(0, 0, DSchedule(ISZ(
    Schedule.Slot(0, maxExecutionTime),
    Schedule.Slot(1, maxExecutionTime),
    Schedule.Slot(2, maxExecutionTime),
    Schedule.Slot(3, maxExecutionTime),
    Schedule.Slot(4, maxExecutionTime),
    Schedule.Slot(5, maxExecutionTime),
    Schedule.Slot(6, maxExecutionTime),
    Schedule.Slot(7, maxExecutionTime),
    Schedule.Slot(8, maxExecutionTime)
  )))

  val defaultDomainToBridgeIdMap: ISZ[Art.BridgeId] = ISZ(
    /* domain 0 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_FC_UART_UARTDriver.id,
    /* domain 1 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_RADIO_RadioDriver_Attestation.id,
    /* domain 2 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_FlyZones_FlyZonesDatabase.id,
    /* domain 3 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_UXAS_UxAS.id,
    /* domain 4 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_WPM_WaypointPlanManagerService.id,
    /* domain 5 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_AM_Gate_CASE_AttestationGate.id,
    /* domain 6 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_FLT_LST_CASE_Filter_LST.id,
    /* domain 7 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_REQ_CASE_Monitor_Req.id,
    /* domain 8 */ Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo.id
  )

  def getStaticSchedulerH(userProvided: MOption[(DScheduleSpec, ISZ[Art.BridgeId], Map[String, Art.BridgeId], CommandProvider)]): StaticScheduler = {
    if (defaultStaticSchedule.schedule.slots.isEmpty && defaultDomainToBridgeIdMap.isEmpty && threadNickNames.isEmpty) {} // line needed for transpiler; do not remove
    userProvided match {
      case MSome((schedule_, domainToBridgeIdMap_, threadNickNames_, commandProvider)) =>
        return getStaticScheduler(schedule_, domainToBridgeIdMap_, threadNickNames_, commandProvider)
      case _ =>
        return getStaticScheduler(
          ScheduleProviderI.getStaticSchedule(),
          // TODO: get the following from extension so they can be customized via C
          defaultDomainToBridgeIdMap,
          threadNickNames,
          DefaultCommandProvider())
    }
  }

  def getStaticScheduler(schedule: DScheduleSpec,
                         domainToBridgeIdMap: ISZ[Art.BridgeId],
                         threadNickNames: Map[String, Art.BridgeId],
                         commandProvider: CommandProvider): StaticScheduler = {
    return StaticScheduler(schedule, Arch.ad.components, domainToBridgeIdMap, threadNickNames,
      if (commandProvider.isInstanceOf[InfoCommandProvider])
        commandProvider.asInstanceOf[InfoCommandProvider].init(
          threadNickNames,
          schedule.schedule.slots.size,
          domainToBridgeIdMap
        )
      else commandProvider)
  }


  /**********************************************************************
   * Legacy Scheduler
   *********************************************************************/

  def getLegacyScheduler(): Legacy = {
    return Legacy(Arch.ad.components)
  }
}

// the purpose of this extension is to allow users to provide custom schedules
// at the C level after transpiling
@ext(name = "ScheduleProvider") object ScheduleProviderI {
  def getRoundRobinOrder(): ISZ[Art.BridgeId] = $

  def getStaticSchedule(): DScheduleSpec = $
}

package uav_june_step6__Linux

import org.sireum._
import art.Art
import art.scheduling.static.Schedule.DScheduleSpec

// Do not edit this file as it will be overwritten if HAMR codegen is rerun

object ScheduleProvider {

  def getRoundRobinOrder(): ISZ[Art.BridgeId] = {
