  def put_keep_out_zones(value : Base_Types.Bits): Unit = {
    Art.insertInInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo.operational_api.keep_out_zones_Id, Base_Types.Bits_Payload(value))
  }

  // setter for in EventDataPort
  def put_observed(value : Base_Types.Bits): Unit = {
    Art.insertInInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo.operational_api.observed_Id, Base_Types.Bits_Payload(value))
  }

  // getter for out EventDataPort
  def get_output(): Option[Base_Types.Bits] = {
    val value: Option[Base_Types.Bits] = get_output_payload() match {
      case Some(Base_Types.Bits_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port output.  Expecting 'Base_Types.Bits_Payload' but received ${v}")
      case _ => None[Base_Types.Bits]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_output_payload(): Option[Base_Types.Bits_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo.initialization_api.output_Id).asInstanceOf[Option[Base_Types.Bits_Payload]]
  }

  // getter for out EventPort
  def get_alert(): Option[art.Empty] = {
    val value: Option[art.Empty] = get_alert_payload() match {
      case Some(art.Empty()) => Some(art.Empty())
      case Some(v) => halt(s"Unexpected payload on port alert.  Expecting 'art.Empty' but received ${v}")
      case _ => None[art.Empty]()
    }
    return value
  }

  // payload getter for out EventPort
  def get_alert_payload(): Option[art.Empty] = {
    return Art.observeOutInfrastructurePort(Arch.UAV_Impl_Instance_MCMP_PROC_SW_MON_GEO_CASE_Monitor_Geo.initialization_api.alert_Id).asInstanceOf[Option[art.Empty]]
  }

}

