object Temperature_i {
  def example(): Integration_Contracts.Temperature_i = {
    return Integration_Contracts.Temperature_i(
      degrees = Base_Types.Float_32_example())
  }

  /** invariant i1
    *   Temperature Data Invariant
    */
  @strictpure def i1_Invariant(value: Integration_Contracts.Temperature_i): B =
    value.degrees >= -459.67f

  /** D-Inv Data Invariant for Integration_Contracts.Temperature_i
    */
  @strictpure def D_Inv_Temperature_i(value: Integration_Contracts.Temperature_i): B =
    (i1_Invariant(value))

  /** D-Inv-Guard Data Invariant for Integration_Contracts.Temperature_i
    */
  @strictpure def D_Inv_Guard_Temperature_i(value: Option[Integration_Contracts.Temperature_i]): B =
    value.nonEmpty -->: D_Inv_Temperature_i(value.get)
}

@datatype class Temperature_i(
  val degrees: F32) {
  @spec def i1 = Invariant(
    degrees >= -459.67f
  )
}

object Temperature_i_Payload {
  def example(): Temperature_i_Payload = {
    return Temperature_i_Payload(Integration_Contracts.Temperature_i.example())
  }
}

