    return Integration_Contracts.SetPoint_i(
      low = Integration_Contracts.Temperature_i.example(),
      high = Integration_Contracts.Temperature_i.example())
  }

  /** invariant i1
    *   SetPoint Data Invariant
    */
  @strictpure def i1_Invariant(value: Integration_Contracts.SetPoint_i): B =
    value.low.degrees >= 70.0f &&
      value.high.degrees <= 100.0f &&
      value.low.degrees <= value.high.degrees

  /** D-Inv Data Invariant for Integration_Contracts.SetPoint_i
    */
  @strictpure def D_Inv_SetPoint_i(value: Integration_Contracts.SetPoint_i): B =
    (i1_Invariant(value))

  /** D-Inv-Guard Data Invariant for Integration_Contracts.SetPoint_i
    */
  @strictpure def D_Inv_Guard_SetPoint_i(value: Option[Integration_Contracts.SetPoint_i]): B =
    value.nonEmpty -->: D_Inv_SetPoint_i(value.get)
}

@datatype class SetPoint_i(
  val low: Integration_Contracts.Temperature_i,
  val high: Integration_Contracts.Temperature_i) {
  @spec def i1 = Invariant(
    low.degrees >= 70.0f &&
      high.degrees <= 100.0f &&
      low.degrees <= high.degrees
