@msig trait TempControl_p_tempControl_TestApi {

  def BeforeEntrypoint(): Unit = {
    Art.initTest(Arch.s_impl_Instance_p_tempControl)
  }

  def AfterEntrypoint(): Unit = {
    Art.finalizeTest(Arch.s_impl_Instance_p_tempControl)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.s_impl_Instance_p_tempControl)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.s_impl_Instance_p_tempControl)
  }

  /** helper function to set the values of all input ports.
   * @param setPoint payloads for event data port setPoint.
   *   ART currently supports single element event data queues so
   *   only the last element of setPoint will be used
   * @param currentTemp payloads for event data port currentTemp.
   *   ART currently supports single element event data queues so
   *   only the last element of currentTemp will be used
   */
  def put_concrete_inputs(setPoint : ISZ[Integration_Contracts.SetPoint_i],
                          currentTemp : ISZ[Integration_Contracts.Temperature_i]): Unit = {
    for(v <- setPoint){
      put_setPoint(v)
    }
    for(v <- currentTemp){
      put_currentTemp(v)
    }
  }


  // setter for in EventDataPort
  def put_setPoint(value : Integration_Contracts.SetPoint_i): Unit = {
    Art.insertInInfrastructurePort(Arch.s_impl_Instance_p_tempControl.operational_api.setPoint_Id, Integration_Contracts.SetPoint_i_Payload(value))
  }

  // setter for in EventDataPort
  def put_currentTemp(value : Integration_Contracts.Temperature_i): Unit = {
    Art.insertInInfrastructurePort(Arch.s_impl_Instance_p_tempControl.operational_api.currentTemp_Id, Integration_Contracts.Temperature_i_Payload(value))
  }

}

