  val dispatchTriggers: Option[ISZ[Art.PortId]],

  setPoint: Port[Integration_Contracts.SetPoint_i],
  currentTemp: Port[Integration_Contracts.Temperature_i]
  ) extends Bridge {

  val ports : Bridge.Ports = Bridge.Ports(
    dataIns = ISZ[art.UPort](),

    dataOuts = ISZ[art.UPort](),

    eventIns = ISZ[art.UPort](setPoint,
                              currentTemp),

    eventOuts = ISZ[art.UPort]()
  )

  val initialization_api : TempControl_Initialization_Api = {
    val api = TempControl_Initialization_Api(
      id,
      setPoint.id,
