object TempControl_Api {
  // assume a2
  //   setPoint Range
  @strictpure def a2(setPoint: Integration_Contracts.SetPoint_i): B =
    setPoint.low.degrees >= 40.0f &&
      setPoint.high.degrees <= 110.0f

  // assume a1
  //   currentTemp Range
  @strictpure def a1(currentTemp: Integration_Contracts.Temperature_i): B =
    currentTemp.degrees >= 70.0f &&
      currentTemp.degrees <= 180.0f
}

import TempControl_Api._

@sig trait TempControl_Api {
  def id: Art.BridgeId
  def setPoint_Id : Art.PortId
  def currentTemp_Id : Art.PortId


  def logInfo(msg: String): Unit = {
    Art.logInfo(id, msg)
  }

  def logDebug(msg: String): Unit = {
    Art.logDebug(id, msg)
  }

  def logError(msg: String): Unit = {
    Art.logError(id, msg)
  }
}

@datatype class TempControl_Initialization_Api (
  val id: Art.BridgeId,
  val setPoint_Id : Art.PortId,
  val currentTemp_Id : Art.PortId) extends TempControl_Api

@datatype class TempControl_Operational_Api (
  val id: Art.BridgeId,
  val setPoint_Id : Art.PortId,
  val currentTemp_Id : Art.PortId) extends TempControl_Api {

  // Logika spec var representing port state for incoming event data port
  @spec var setPoint: Option[Integration_Contracts.SetPoint_i] = $
  @spec def setPoint_Inv = Invariant(
    setPoint.isEmpty || a2(setPoint.get)
  )

  def get_setPoint() : Option[Integration_Contracts.SetPoint_i] = {
    Contract(
      Ensures(
        setPoint.isEmpty || a2(setPoint.get),
        Res == setPoint
      )
    )
    val value : Option[Integration_Contracts.SetPoint_i] = Art.getValue(setPoint_Id) match {
      case Some(Integration_Contracts.SetPoint_i_Payload(v)) => Some(v)
      case Some(v) =>
        Art.logError(id, s"Unexpected payload on port setPoint.  Expecting 'Integration_Contracts.SetPoint_i_Payload' but received ${v}")
        None[Integration_Contracts.SetPoint_i]()
      case _ => None[Integration_Contracts.SetPoint_i]()
    }
    return value
  }

  // Logika spec var representing port state for incoming event data port
  @spec var currentTemp: Option[Integration_Contracts.Temperature_i] = $
  @spec def currentTemp_Inv = Invariant(
    currentTemp.isEmpty || a1(currentTemp.get)
  )

  def get_currentTemp() : Option[Integration_Contracts.Temperature_i] = {
    Contract(
      Ensures(
        currentTemp.isEmpty || a1(currentTemp.get),
        Res == currentTemp
      )
    )
    val value : Option[Integration_Contracts.Temperature_i] = Art.getValue(currentTemp_Id) match {
      case Some(Integration_Contracts.Temperature_i_Payload(v)) => Some(v)
      case Some(v) =>
        Art.logError(id, s"Unexpected payload on port currentTemp.  Expecting 'Integration_Contracts.Temperature_i_Payload' but received ${v}")
        None[Integration_Contracts.Temperature_i]()
      case _ => None[Integration_Contracts.Temperature_i]()
    }
    return value
  }
}

