@sig trait tempSensor_Api {
  def id: Art.BridgeId
  def currentTemp_Id : Art.PortId

  // Logika spec var representing port state for outgoing event data port
  @spec var currentTemp: Option[Integration_Contracts.Temperature_i] = $
  @spec def currentTemp_Inv = Invariant(
    currentTemp.isEmpty || g1(currentTemp.get)
  )

  def put_currentTemp(value : Integration_Contracts.Temperature_i) : Unit = {
    Contract(
      Requires(
        g1(value)
      ),
      Modifies(currentTemp),
      Ensures(
        currentTemp == Some(value)
      )
    )
    Spec {
      currentTemp = Some(value)
    }

    Art.putValue(currentTemp_Id, Integration_Contracts.Temperature_i_Payload(value))
  }

  def logInfo(msg: String): Unit = {
    Art.logInfo(id, msg)
  }

  def logDebug(msg: String): Unit = {
    Art.logDebug(id, msg)
  }

  def logError(msg: String): Unit = {
    Art.logError(id, msg)
  }
}

