void sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_enqueue(
  sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_t *queue,
  isolette_Isolette_Data_Model_Regulator_Mode_Type *data) {

  // Simple ring with one dirty element that will be written next. Only one
  // writer, so no need for any synchronization.
  // elt[queue->numSent % SB_QUEUE_ISOLETTE_ISOLETTE_DATA_MODEL_REGULATOR_MODE_TYPE_1_SIZE]
  // is always considered dirty. So do not advance queue->NumSent
  // till AFTER data is copied.

  size_t index = queue->numSent % SB_QUEUE_ISOLETTE_ISOLETTE_DATA_MODEL_REGULATOR_MODE_TYPE_1_SIZE;

  queue->elt[index] = *data; // Copy data into queue

  // Release memory fence - ensure that data write above completes BEFORE we advance queue->numSent
