// Receiver API
//
// See sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1.h for API documentation. Only implementation details are documented here.

void sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_Recv_init(
  sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_Recv_t *recvQueue,
  sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_t *queue) {

  recvQueue->numRecv = 0;
  recvQueue->queue = queue;
}

bool sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_dequeue(
  sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_Recv_t *recvQueue,
  sb_event_counter_t *numDropped,
  isolette_Isolette_Data_Model_Regulator_Mode_Type *data) {

  sb_event_counter_t *numRecv = &recvQueue->numRecv;
  sb_queue_isolette_Isolette_Data_Model_Regulator_Mode_Type_1_t *queue = recvQueue->queue;

  // Get a copy of numSent so we can see if it changes during read
  sb_event_counter_t numSent = queue->numSent;

  // Acquire memory fence - ensure read of queue->numSent BEFORE reading data
  __atomic_thread_fence(__ATOMIC_ACQUIRE);

  // How many new elements have been sent? Since we are using unsigned
  // integers, this correctly computes the value as counters wrap.
  sb_event_counter_t numNew = numSent - *numRecv;
  if (0 == numNew) {
    // Queue is empty
    return false;
  }

  // One element in the ring buffer is always considered dirty. Its the next
  // element we will write.  It's not safe to read it until numSent has been
  // incremented. Thus there are really only (SB_QUEUE_ISOLETTE_ISOLETTE_DATA_MODEL_REGULATOR_MODE_TYPE_1_SIZE - 1)
  // elements in the queue.
  *numDropped = (numNew <= SB_QUEUE_ISOLETTE_ISOLETTE_DATA_MODEL_REGULATOR_MODE_TYPE_1_SIZE - 1) ? 0 : numNew - SB_QUEUE_ISOLETTE_ISOLETTE_DATA_MODEL_REGULATOR_MODE_TYPE_1_SIZE + 1;

  // Increment numRecv by *numDropped plus one for the element we are about to read.
  *numRecv += *numDropped + 1;

  // UNUSED - number of elements left to be consumed
  //sb_event_counter_t numRemaining = numSent - *numRecv;

  size_t index = (*numRecv - 1) % SB_QUEUE_ISOLETTE_ISOLETTE_DATA_MODEL_REGULATOR_MODE_TYPE_1_SIZE;
  *data = queue->elt[index]; // Copy data

  // Acquire memory fence - ensure read of data BEFORE reading queue->numSent again
  __atomic_thread_fence(__ATOMIC_ACQUIRE);

  if (queue->numSent - *numRecv + 1 < SB_QUEUE_ISOLETTE_ISOLETTE_DATA_MODEL_REGULATOR_MODE_TYPE_1_SIZE) {
    // Sender did not write element we were reading. Copied data is coherent.
    return true;
  } else {
    // Sender may have written element we were reading. Copied data may be incoherent.
    // We dropped the element we were trying to read, so increment *numDropped.
    ++(*numDropped);
    return false;
  }
}

