#pragma once

#include <stdbool.h>
#include <stdint.h>

// Do not edit this file as it will be overwritten if codegen is rerun

#define event_data_port_port_queues_ArrayOfInts_BYTE_SIZE 40
#define event_data_port_port_queues_ArrayOfInts_DIM_0 10

typedef int32_t event_data_port_port_queues_ArrayOfInts [event_data_port_port_queues_ArrayOfInts_DIM_0];

typedef struct event_data_port_port_queues_struct_i {
  int32_t size;
  event_data_port_port_queues_ArrayOfInts elements;
} event_data_port_port_queues_struct_i;

#pragma once

#include <stdint.h>

// Do not edit this file as it will be overwritten if codegen is rerun

typedef _Atomic uintmax_t sb_event_counter_t;

/*
