testComputeCB_macro {
  (
    $test_name: ident,
    config: $config:expr,
    api_EthernetFramesRxIn: $api_EthernetFramesRxIn_strat:expr,
    api_EthernetFramesTxIn: $api_EthernetFramesTxIn_strat:expr
  ) => {
    proptest!{
      #![proptest_config($config)]
      #[test]
      #[serial]
      fn $test_name(
        (api_EthernetFramesRxIn, api_EthernetFramesTxIn)
            in ($api_EthernetFramesRxIn_strat, $api_EthernetFramesTxIn_strat)
      ) {
        match$crate::test::util::cb_apis::testComputeCB(api_EthernetFramesRxIn, api_EthernetFramesTxIn) {
          $crate::test::util::cb_apis::HarnessResult::RejectedPrecondition => {
            return Err(proptest::test_runner::TestCaseError::reject(
              "Precondition failed: invalid input combination",
            ))
          }
          $crate::test::util::cb_apis::HarnessResult::FailedPostcondition(e) => {
            return Err(e)
          }
          $crate::test::util::cb_apis::HarnessResult::Passed => { }
        }
      }
    }
  };
}
