package prod_cons__JVM.ProdConsFlows

import org.sireum._
import prod_cons__JVM.ProdConsFlows._
import prod_cons__JVM.GumboXUtil
import prod_cons__JVM.GumboXUtil.GumboXResult
import prod_cons__JVM.RandomLib
import org.sireum.Random.Gen64
import org.sireum.Random.Impl.Xoshiro256

// Do not edit this file as it will be overwritten if HAMR codegen is rerun
class Filter_p_p_filterp_GumboX_Tests extends Filter_p_p_filterp_GumboX_TestHarness_ScalaTest {

  // set failOnUnsatPreconditions to T if the unit tests should fail when either
  // SlangCheck is never able to satisfy a datatype's filter or the generated
  // test vectors are never able to satisfy an entry point's assume pre-condition
  var failOnUnsatPreconditions: B = F

  var verbose: B = F

  val seedGen: Gen64 = Random.Gen64Impl(Xoshiro256.create)
  val ranLibd_event_in: RandomLib = RandomLib(Random.Gen64Impl(Xoshiro256.createSeed(seedGen.genU64())))
  val ranLibb_event_data_in: RandomLib = RandomLib(Random.Gen64Impl(Xoshiro256.createSeed(seedGen.genU64())))
  val ranLibc_event_data_in: RandomLib = RandomLib(Random.Gen64Impl(Xoshiro256.createSeed(seedGen.genU64())))
  val ranLiba_data_in: RandomLib = RandomLib(Random.Gen64Impl(Xoshiro256.createSeed(seedGen.genU64())))

  def next(): Option[Filter_p_p_filterp_PreState_Container_P] = {
    try {
      val api_d_event_in = ranLibd_event_in.nextOption_artEmpty()
      val api_b_event_data_in = ranLibb_event_data_in.nextOptionProdConsFlowsContainer_i()
      val api_c_event_data_in = ranLibc_event_data_in.nextOptionProdConsFlowsContainer_i()
      val api_a_data_in = ranLiba_data_in.nextProdConsFlowsContainer_i()

      return Some(Filter_p_p_filterp_PreState_Container_P(api_d_event_in,api_b_event_data_in,api_c_event_data_in,api_a_data_in))
    } catch {
      case e: AssertionError =>
       // SlangCheck was unable to satisfy a datatype's filter
       return None()
    }
  }

  {
    for (i <- 0 to GumboXUtil.numTests) {
      this.registerTest(s"testInitialiseCB_$i") {
        testInitialiseCB() match {
          case GumboXResult.Pre_Condition_Unsat =>
            halt("Infeasible as initialize entry points cannot contain assume clauses and cannot access incoming ports or state variables")
          case GumboXResult.Post_Condition_Fail =>
            fail ("Post condition did not hold")
          case GumboXResult.Post_Condition_Pass =>
            if (verbose) {
              println ("Success!")
            }
        }
      }
    }
  }

  {
    for (i <- 0 to GumboXUtil.numTests) {
      this.registerTest(s"testComputeCB_$i") {
        var retry: B = T

        var j: Z = 0
        while (j < GumboXUtil.numTestVectorGenRetries && retry) {
          next() match {
            case Some(o) =>

              if (verbose && j > 0) {
                println(s"Retry $j:")
              }

              val results = testComputeCBV(o)

              if (verbose) {
                val tq = "\"\"\""
                println(st"""Replay Unit Test:
                            |  test("Replay testComputeCB_$i") {
                            |    val json = st${tq}${prod_cons__JVM.JSON.fromProdConsFlowsFilter_p_p_filterp_PreState_Container_P(o, T)}${tq}.render
                            |    val testVector = prod_cons__JVM.JSON.toProdConsFlowsFilter_p_p_filterp_PreState_Container_P(json).left
                            |    assert (testComputeCBV(testVector) == prod_cons__JVM.GumboXUtil.GumboXResult.$results)
                            |  }""".render)
              }

              results match {
                case GumboXResult.Pre_Condition_Unsat =>
                case GumboXResult.Post_Condition_Fail =>
                  fail ("Post condition did not hold")
                  retry = F
                case GumboXResult.Post_Condition_Pass =>
                  if (verbose) {
                    println ("Success!")
                  }
                  retry = F
              }
            case _ =>
          }
          j = j + 1
        }

        if (retry) {
          if (failOnUnsatPreconditions) {
            fail ("Unable to satisfy precondition")
          } else if (verbose) {
            cprintln(T, "Unable to satisfy precondition")
          }
        }
      }
    }
  }
}